from dotenv import load_dotenv
load_dotenv()

import argparse
import rich
from rich.padding import Padding
import os
from execution.inputs import get_user_inputs, validate_user_inputs
from execution.orchestrator import execute_crews, get_execution_config
from models import RuntimeSettings
from pathlib import Path
from execution.consts import EXECUTION_CONFIG_PATH
from utils import EnvironmentVariableNotSetError, is_safe_path

class KeyValueAction(argparse.Action):
    def __call__(self, parser, namespace, values, option_string=None):
        setattr(namespace, self.dest, {})
        for value in values:
            key, value = value.split('=')
            getattr(namespace, self.dest)[key] = value

def parse_arguments():
    parser = argparse.ArgumentParser("crews_control")

    parser.add_argument("--project-name", help="The name of the project to run.", type=str)
    parser.add_argument("--ignore-cache", help="Ignore the cache and run all crews", action="store_true")
    parser.add_argument('--params', nargs='+', action=KeyValueAction, help='List of key=value pairs')

    group = parser.add_mutually_exclusive_group()
    group.add_argument("--list-tools", help="List available tools", action="store_true")
    group.add_argument("--list-models", help="List available models", action="store_true")
    group.add_argument("--list-projects", help="List available projects", action="store_true")
    group.add_argument("--benchmark", help="Run the project from benchmark file (`benchmark.yml`)", action="store_true")

    args = parser.parse_args()

    # Ensure project name is provided if not listing
    if not (args.list_tools or args.list_models or args.list_projects):
        if not args.project_name:
            parser.error("--project-name is required for execution and benchmark")

    return args

def handle_list_arguments(args):
    if args.list_tools:
        from utils import list_tools
        list_tools()
        os._exit(0)
    elif args.list_models:
        from utils import list_models
        list_models()
        os._exit(0)
    elif args.list_projects:
        from utils import list_projects
        list_projects()
        os._exit(0)

def display_error(message):
    rich.print(Padding(f"[bold red]Error: {message}[/bold red]", (2, 4), expand=True, style="bold red"))
    os._exit(1)

def display_message(message):
    rich.print(Padding(f"[bold white]{message}[/bold white]", (2, 4), expand=True, style="bold white"))

def execute_project(runtime_settings, execution_config, user_inputs=None, validations=None):
    try:
        validate_user_inputs(user_inputs=user_inputs or {}, execution_config=execution_config)
    except ValueError as e:
        display_error(str(e))
    execute_crews(
        project_name=runtime_settings.project_name,
        user_inputs=user_inputs or get_user_inputs(execution_config),
        validations=validations,
        ignore_cache=runtime_settings.ignore_cache
    )

def main():
    args = parse_arguments()
    handle_list_arguments(args)

    runtime_settings = RuntimeSettings(
        project_name=args.project_name,
        benchmark_mode=args.benchmark,
        ignore_cache=args.ignore_cache
    )

    project_path = Path.cwd() / 'projects' / runtime_settings.project_name
    if not project_path.exists():
        display_error(f"Project {runtime_settings.project_name} not found")

    try:
        execution_config = get_execution_config(project_name=runtime_settings.project_name)
    except FileNotFoundError:
        display_error(f"{EXECUTION_CONFIG_PATH} file not found for project {runtime_settings.project_name}")

    display_message(f"Welcome to {runtime_settings.project_name}â„¢")

    try:
        if runtime_settings.benchmark_mode:
            from utils import report_success_percentage
            benchmark_settings = runtime_settings.load_benchmark_file()
            for index, execution in enumerate(benchmark_settings.get('executions') or []):
                rich.print(f"[grey]Running benchmark execution: <{index}>[/grey]")
                execute_project(runtime_settings, execution_config, execution.get('user_inputs'), execution.get('validations'))
        elif args.params:
            user_inputs = {k: v for k, v in args.params.items()}
            execute_project(runtime_settings, execution_config, user_inputs)
        else:
            # Interactive mode: get user inputs interactively
            user_inputs = get_user_inputs(execution_config)
            execute_project(runtime_settings, execution_config, user_inputs)
    except (FileNotFoundError, EnvironmentVariableNotSetError) as e:
        display_error(str(e))

    if runtime_settings.benchmark_mode:
        if not is_safe_path(Path.cwd() / 'projects', Path.cwd() / 'projects' / runtime_settings.project_name / 'validations'):
            display_error(f"Path traversal detected in project name {runtime_settings.project_name}")

        report_success_percentage(f"projects/{runtime_settings.project_name}/validations")

if __name__ == "__main__":
    main()

import os
from langchain_openai import AzureOpenAIEmbeddings
from langchain_openai import AzureChatOpenAI
from langchain_groq import ChatGroq
from langchain_anthropic import ChatAnthropic
from langchain_community.embeddings import GPT4AllEmbeddings
import json
from pathlib import Path
import re
from langchain_community.embeddings import HuggingFaceEmbeddings

class EnvironmentVariableNotSetError(Exception):
    pass


def sanitize_filename(filename: str) -> str:
    """Sanitize the filename by replacing non-alphanumeric characters with underscores."""
    return re.sub(r'[^a-zA-Z0-9]', '_', filename).lower()

def is_safe_path(base_dir: Path, path: Path) -> bool:
    """Check if the resolved path is within the base directory to prevent path traversal."""
    try:
        return path.resolve().is_relative_to(base_dir.resolve())
    except ValueError:
        return False

def validate_env_vars(*vars):
    # Handle single list or tuple containing a list
    if len(vars) == 1 and isinstance(vars[0], list):
        vars = vars[0]

    for var in vars:
        if os.getenv(var) is None or os.getenv(var) == "":
            raise EnvironmentVariableNotSetError(f"Environment variable '{var}' is not set.")

def create_llm_client(config):
    provider = config['provider']
    validate_env_vars(config['required_vars'])

    if provider == 'groq':
        return ChatGroq(
            model=os.getenv("GROQ_MODEL_NAME"),
            api_key=os.getenv("GROQ_API_KEY"),
            streaming=config.get('stream', True),
            max_tokens=config.get('max_tokens', 8192),
            model_name=os.getenv('GROQ_MODEL_NAME'),
        )
    elif provider == 'anthropic':
        return ChatAnthropic(
            model=os.getenv("ANTHROPIC_MODEL_NAME"),
            temperature=config.get('temperature', 0.7),
            max_tokens=config.get('max_tokens', 1024),
            timeout=None,
            max_retries=2,
        )
    elif provider == 'azure_openai':
        return AzureChatOpenAI(
            temperature=config.get('temperature', 0),
            openai_api_version=os.getenv("AZURE_OPENAI_VERSION"),
            azure_deployment=os.getenv("AZURE_OPENAI_DEPLOYMENT"),
            api_key=os.getenv("AZURE_OPENAI_KEY"),
            azure_endpoint=os.getenv("AZURE_OPENAI_ENDPOINT"),
        )
    elif provider == 'openai':
        from langchain_openai import ChatOpenAI
        return ChatOpenAI(
            temperature=config.get('temperature', 0),
            model=os.getenv("OPENAI_MODEL_NAME"),
            api_key=os.getenv("OPENAI_API_KEY"),
        )
    # Add more LLM providers here as needed
    else:
        raise ValueError(f"Unsupported LLM provider: {provider}")

def create_embedder_client(config):
    provider = config['provider']

    if provider == 'gpt4all':
        return GPT4AllEmbeddings(provider="gpt4all")
    elif provider == 'azure_openai':
        import warnings
        from langchain_core._api.deprecation import LangChainDeprecationWarning
        warnings.filterwarnings(
            "ignore",
            category=LangChainDeprecationWarning,
            message="The class `AzureChatOpenAI` was deprecated"
        )
        warnings.filterwarnings(
            "ignore",
            category=LangChainDeprecationWarning,
            message="The class `AzureOpenAIEmbeddings` was deprecated"
        )
        warnings.filterwarnings(
            "ignore",
            category=LangChainDeprecationWarning,
            message="The method `BaseChatModel.__call__` was deprecated"
        )
        validate_env_vars(config['required_vars'])
        return AzureOpenAIEmbeddings(
            azure_deployment=os.getenv("AZURE_OPENAI_EMBEDDING_DEPLOYMENT_NAME"),
            openai_api_version=os.getenv("OPENAI_API_VERSION"),
            azure_endpoint=os.getenv("AZURE_OPENAI_ENDPOINT"),
            api_key=os.getenv("AZURE_OPENAI_API_KEY"),
        )
    elif provider == 'huggingface':
        import warnings
        warnings.filterwarnings("ignore", category=FutureWarning, message="`resume_download` is deprecated and will be removed in version 1.0.0. Downloads always resume when possible. If you want to force a new download, use `force_download=True`.")

        return HuggingFaceEmbeddings(
            model_name=config['config']['model'],
        )
    elif provider == 'openai':
        from langchain_openai import OpenAIEmbeddings
        import warnings
        from langchain_core._api.deprecation import LangChainDeprecationWarning
        warnings.filterwarnings(
            "ignore",
            category=LangChainDeprecationWarning,
            message="The class ChatOpenAI was deprecated"
        )
        warnings.filterwarnings(
            "ignore",
            category=LangChainDeprecationWarning,
            message="The class OpenAIEmbeddings was deprecated"
        )
        warnings.filterwarnings(
            "ignore",
            category=LangChainDeprecationWarning,
            message="The method BaseChatModel.__call__ was deprecated"
        )
        validate_env_vars(config['required_vars'])
        return OpenAIEmbeddings(
            model=os.getenv("OPENAI_EMBEDDING_MODEL_NAME"),
            openai_api_version=os.getenv("OPENAI_API_VERSION"),
            api_key=os.getenv("OPENAI_API_KEY"),
        )
    # Add more embedder providers here as needed
    else:
        raise ValueError(f"Unsupported embedder provider: {provider}")

def get_clients(llm_name: str, embedder_name: str):
    llm_config_path = Path('config') / 'llms' / f'{llm_name}.json'
    embedder_config_path = Path('config') / 'embedders' / f'{embedder_name}.json'

    llm_config = load_config(llm_config_path)
    embedder_config = load_config(embedder_config_path)

    llm_client = create_llm_client(llm_config)
    embedder_client = create_embedder_client(embedder_config)

    return llm_client, embedder_client

def load_config(file_path):
    with open(file_path, 'r') as file:
        return json.load(file)

def get_embedchain_settings(task_id: str, llm_name: str, embedder_name: str) -> dict:
    llm_config_path = Path('config') / 'llms' / f'{llm_name}.json'
    embedder_config_path = Path('config') / 'embedders' / f'{embedder_name}.json'

    llm_config = load_config(llm_config_path)
    embedder_config = load_config(embedder_config_path)

    if llm_name == 'azure_openai':
        llm, embedder = get_clients(llm_name=llm_name, embedder_name=embedder_name)
        llm_config['config']['deployment_name'] = getattr(llm, 'deployment_name')
        llm_config['config']['api_key'] = getattr(llm, 'openai_api_key')
        embedder_config['config']['deployment_name'] = getattr(embedder, 'deployment')
        embedder_config['config']['api_key'] = getattr(embedder, 'openai_api_key')
    elif llm_name == 'openai':
        llm, embedder = get_clients(llm_name=llm_name, embedder_name=embedder_name)
        llm_config['config']['api_key'] = getattr(llm, 'openai_api_key')
        embedder_config['config']['api_key'] = getattr(embedder, 'openai_api_key')

    return {
        'llm': {
            'provider': llm_config['provider'],
            'config': llm_config['config'],
        },
        'embedder': {
            'provider': embedder_config['provider'],
            'config': embedder_config['config'],
        },
        'app': {
            'config': {
                'id': task_id,
                'collect_metrics': False,
            },
        },
    }


def get_validated_input(prompt: str, valid_options: list[str] = None) -> str:
    while True:
        user_input = input(prompt).strip()
        if not user_input:
            print("Input cannot be empty.")
        elif valid_options is not None and user_input not in valid_options:
            print(f"Please enter a valid option from {valid_options}")
        else:
            return user_input

def report_success_percentage(folder_path):
    total_files = 0
    success_files = 0
    failed_details = []

    # Iterate through all files in the given directory
    for filename in os.listdir(folder_path):
        if filename.endswith(".result"):
            total_files += 1
            path: Path = Path.cwd() / folder_path / filename
            with open(path, 'r') as file:
                try:
                    data = json.load(file)
                except json.JSONDecodeError:
                    print(f"Error reading file {filename}")
                    failed_details.append((filename, ["Error reading file"]))
                    continue

                # Check each metric in the file
                has_failure = False
                file_failures = []
                for metric, verdict in data.items():
                    if not verdict.get("res", False):  # Check if the result is False or absent
                        has_failure = True
                        reason = verdict.get("reason", "No reason provided")
                        file_failures.append(f"{metric}: {reason}")

                if has_failure:
                    failed_details.append((filename, file_failures))
                else:
                    success_files += 1

    # Calculate success percentage
    if total_files > 0:
        success_percentage = (success_files / total_files) * 100
    else:
        success_percentage = 100  # Default to 100% if no .result files are found

    print(f"Success percentage: {success_percentage:.2f}%")
    if failed_details:
        print("Failed files and reasons:")
        for filename, failures in failed_details:
            print(f"{filename}:")
            for failure in failures:
                print(f"  {failure}")
    else:
        print("All files succeeded!")

