from abc import ABC, abstractmethod
from typing import List, Dict, Any, Union
import importlib
import json

AVAILABLE_DATASETS: List[str] = [
    "change.SpotTheDifference",
    "change.CocoSpotTheDifference",
    "video.DenseCaptions",
    "video.TVCaptions",
    "video.VisualStoryTelling",
    "3d.SceneNavigation",
    "funqa.FunQA_translation",
