    def _load_query_inputs(self, path: str) -> list[str]:
        with open(path, "r") as f:
            json_data = json.load(f)
        results = []
        for inner_dict in json_data:
            file_id = inner_dict["img_id"]
            sentences = inner_dict["sentences"]
            results.append(
                {
                    "id": file_id,
                    "sentences": "\n".join(sentences),
                }
