import datetime

import app.routers.calendar_grid as cg

DATE = datetime.date(1988, 5, 3)
DAY = cg.Day(datetime.date(1988, 5, 3))
WEEKEND = cg.DayWeekend(datetime.date(2021, 1, 23))
N_DAYS = 3
N_DAYS_BEFORE = datetime.date(1988, 4, 30)
NEXT_N_DAYS = [
    cg.Day(datetime.date(1988, 5, 4)),
    cg.Day(datetime.date(1988, 5, 5)),
    cg.Day(datetime.date(1988, 5, 6))
]
DAY_TYPES = [cg.Day, cg.DayWeekend, cg.Today, cg.FirstDayMonth]
WEEK_DAYS = cg.Week.WEEK_DAYS


class TestCalendarGrid:
    @staticmethod
    def test_get_calendar(client):
        response = client.get("/calendar/month")
        assert response.ok
        assert b"SUNDAY" in response.content

    @staticmethod
    def test_get_calendar_extends(client):
        days = 42
        response = client.get(
            f"/calendar/month/add/{DAY.set_id()}?days={days}"
        )
        assert response.ok
        assert b"08-May" in response.content

    @staticmethod
    def test_create_day():
        dates_to_check = {
            'normal_day': datetime.date(2021, 1, 20),
            'weekend': datetime.date(2021, 1, 23),
            'today': datetime.date.today(),
            'first_month': datetime.date(2021, 1, 1)
        }

        for i, value in enumerate(dates_to_check.values()):
            assert isinstance(cg.create_day(value), DAY_TYPES[i])

    @staticmethod
    def test_get_next_date():
        next_day_generator = cg.get_next_date(DATE)
        next_day = next(next_day_generator, None)
        next_day_check = cg.Day(DATE + datetime.timedelta(days=1))
        assert next_day
        assert isinstance(next_day, cg.Day)
        assert next_day.date == next_day_check.date

    @staticmethod
    def test_get_date_before_n_days():
        assert cg.get_date_before_n_days(DATE, N_DAYS) == N_DAYS_BEFORE

    @staticmethod
    def test_get_first_day_month_block(Calendar):
        assert (
            cg.get_first_day_month_block(DATE)
            == next(Calendar.itermonthdates(DATE.year, DATE.month))
        )

    @staticmethod
    def test_get_n_days():
        next_n_dates = cg.get_n_days(DATE, N_DAYS)
        for i in range(N_DAYS):
            assert next(next_n_dates).date == NEXT_N_DAYS[i].date

    @staticmethod
    def test_create_weeks():
        week = cg.create_weeks(NEXT_N_DAYS, cg.Week.WEEK_DAYS)
        assert week
        assert isinstance(week[0], cg.Week)
        assert isinstance(week[0].days[0], cg.Day)
        assert len(week) == 1 and len(week[0].days) == 3

    @staticmethod
    def test_get_month_block(Calendar):
        month_weeks = cg.create_weeks(
            Calendar.itermonthdates(1988, 5), WEEK_DAYS)
        get_block = cg.get_month_block(cg.Day(DATE), n=len(month_weeks))

        for i in range(len(month_weeks)):
            for j in range(cg.Week.WEEK_DAYS):
                assert get_block[i].days[j].date == month_weeks[i].days[j]

    @staticmethod
    def test_get_user_local_time():
        time_string = "%b%w%Y"
        server_time = cg.Day.get_user_local_time()
        server_time_check = datetime.datetime.today()
        assert server_time
        assert server_time.strftime(
            time_string) == server_time_check.strftime(time_string)

    @staticmethod
    def test_is_weekend():
        assert not cg.Day.is_weekend(DATE)
        assert cg.Day.is_weekend(WEEKEND.date)

    @staticmethod
    def test_display_day():
        assert DAY.display() == '03 MAY 88'

    @staticmethod
    def test_set_id():
        assert DAY.set_id() == '03-May-1988'

    @staticmethod
    def test_display_str():
        assert str(DAY) == '03'

    @staticmethod
    def test_create_week_object():
        assert cg.Week(NEXT_N_DAYS)

import pytest
from sqlalchemy.exc import SQLAlchemyError
from sqlalchemy.testing import mock
from starlette import status
from starlette.datastructures import ImmutableMultiDict

from app.database.models import Event
from app.routers.categories import (get_user_categories,
                                    validate_request_params,
                                    validate_color_format)


class TestCategories:
    CATEGORY_ALREADY_EXISTS_MSG = "category is already exists for"
    UNALLOWED_PARAMS = "contains unallowed params"
    BAD_COLOR_FORMAT = "if not from expected format"

    @staticmethod
    def test_get_categories_logic_succeeded(session, user, category):
        assert get_user_categories(session, category.user_id) == [category]

    @staticmethod
    def test_creating_new_category(client, user):
        response = client.post("/categories/",
                               json={"user_id": user.id, "name": "Foo",
                                     "color": "eecc11"})
        assert response.ok
        assert {("user_id", user.id), ("name", "Foo"),
                ("color", "eecc11")}.issubset(
            set(response.json()['category'].items()))

    @staticmethod
    def test_creating_not_unique_category_failed(client, sender, category):
        response = client.post("/categories/", json={"user_id": sender.id,
                                                     "name": "Guitar Lesson",
                                                     "color": "121212"})
        assert response.status_code == status.HTTP_400_BAD_REQUEST
        assert TestCategories.CATEGORY_ALREADY_EXISTS_MSG in \
               response.json()["detail"]

    @staticmethod
    def test_creating_new_category_bad_color_format(client, user):
        response = client.post("/categories/",
                               json={"user_id": user.id, "name": "Foo",
                                     "color": "bad format"})
        assert response.status_code == status.HTTP_400_BAD_REQUEST
        assert TestCategories.BAD_COLOR_FORMAT in response.json()["detail"]

    @staticmethod
    def test_create_event_with_category(category):
        event = Event(title="OOO", content="Guitar rocks!!",
                      owner_id=category.user_id, category_id=category.id)
        assert event.category_id is not None
        assert event.category_id == category.id

    @staticmethod
    def test_update_event_with_category(today_event, category):
        assert today_event.category_id is None
        today_event.category_id = category.id
        assert today_event.category_id is not None
        assert today_event.category_id == category.id

    @staticmethod
    def test_get_user_categories(client, category):
        response = client.get(f"/categories/?user_id={category.user_id}"
                              f"&name={category.name}&color={category.color}")
        assert response.ok
        assert len(response.json()) == 1
        assert set(response.json()[0].items()) == {
            ("user_id", category.user_id), ("color", "121212"),
            ("name", "Guitar Lesson"), ("id", category.id)}

    @staticmethod
    def test_get_category_by_name(client, sender, category):
        response = client.get(f"/categories/?user_id={category.user_id}"
                              f"&name={category.name}")
        assert response.ok
        assert len(response.json()) == 1
        assert set(response.json()[0].items()) == {
            ("user_id", category.user_id), ("color", "121212"),
            ("name", "Guitar Lesson"), ("id", category.id)}

    @staticmethod
    def test_get_category_by_color(client, sender, category):
        response = client.get(f"/categories/?user_id={category.user_id}&"
                              f"color={category.color}")
        assert response.ok
        assert len(response.json()) == 1
        assert set(response.json()[0].items()) == {
            ("user_id", category.user_id), ("color", "121212"),
            ("name", "Guitar Lesson"), ("id", category.id)}

    @staticmethod
    def test_get_category_bad_request(client):
        response = client.get("/categories/")
        assert response.status_code == status.HTTP_400_BAD_REQUEST
        assert TestCategories.UNALLOWED_PARAMS in response.json()["detail"]

    @staticmethod
    def test_repr(category):
        assert category.__repr__() == \
               f'<Category {category.id} {category.name} {category.color}>'

    @staticmethod
    def test_to_dict(category):
        assert {c.name: getattr(category, c.name) for c in
                category.__table__.columns} == category.to_dict()

    @staticmethod
    @pytest.mark.parametrize('params, expected_result', [
        (ImmutableMultiDict([('user_id', ''), ('name', ''),
                             ('color', 'aabbcc')]), True),
        (ImmutableMultiDict([('user_id', ''), ('name', '')]), True),
        (ImmutableMultiDict([('user_id', ''), ('color', 'aabbcc')]), True),
        (ImmutableMultiDict([('user_id', '')]), True),
        (ImmutableMultiDict([('name', ''), ('color', 'aabbcc')]), False),
        (ImmutableMultiDict([]), False),
        (ImmutableMultiDict([('user_id', ''), ('name', ''),
                             ('color', 'aabbcc'), ('bad_param', '')]), False),
    ])
    def test_validate_request_params(params, expected_result):
        assert validate_request_params(params) == expected_result

    @staticmethod
    @pytest.mark.parametrize('color, expected_result', [
        ("aabbcc", True),
        ("110033", True),
        ("114b33", True),
        ("", False),
        ("aabbcg", False),
        ("aabbc", False),
    ])
    def test_validate_color_format(color, expected_result):
        assert validate_color_format(color) == expected_result

    @staticmethod
    def test_get_categories_failed(session):
        def raise_error(param):
            raise SQLAlchemyError()

        session.query = mock.Mock(side_effect=raise_error)
        assert get_user_categories(session, 1) == []

