from enum import Enum


class RequestType(Enum):
    BENCHMARK = "benchmark_request"
    RWR = "real_world_request"

    def __str__(self) -> str:
        if self == RequestType.BENCHMARK:
            return "Benchmark"
        elif self == RequestType.RWR:
            return "Real World Request"
        else:
            raise ValueError(f"Unknown request type: {self}")


class ValidatorMessage(Enum):
    WINDDOWN = "winddown"
    WINDDOWN_COMPLETE = "winddown_complete"
    COMPETITION_COMPLETE = "competition_complete"

    def __str__(self) -> str:
        return self.value

import os
import json
import time
import shutil
import bittensor as bt
from ..models.sota import SotaState
from ..models.neuron import NeuronState
import torch


class SotaManager:
    def __init__(self, sota_directory: str):
        self.sota_directory = sota_directory
        self.sota_state_path = os.path.join(sota_directory, "sota_state.json")
        self.sota_state = self._load_state()

    def _load_state(self) -> SotaState:
        try:
            if os.path.exists(self.sota_state_path):
                with open(self.sota_state_path, "r") as f:
                    data = json.load(f)
                    return SotaState(**data)
            return SotaState()
        except Exception as e:
            bt.logging.error(f"Error loading SOTA state: {e}")
            return SotaState()

    def _save_state(self):
        try:
            with open(self.sota_state_path, "w") as f:
                json.dump(self.sota_state.__dict__, f, indent=4)
        except Exception as e:
            bt.logging.error(f"Error saving SOTA state: {e}")

    def preserve_circuit(
        self,
        circuit_dir: str,
        neuron_state: NeuronState,
        miner_states: dict[str, NeuronState],
    ):
        try:
            sota_circuit_dir = os.path.join(self.sota_directory, "circuit")
            if os.path.exists(sota_circuit_dir):
                shutil.rmtree(sota_circuit_dir)
            shutil.copytree(circuit_dir, sota_circuit_dir)

            self.sota_state = SotaState(
                sota_relative_score=neuron_state.sota_relative_score,
                hash=neuron_state.hash,
                hotkey=neuron_state.hotkey,
                uid=neuron_state.uid,
                proof_size=neuron_state.proof_size,
                response_time=neuron_state.response_time,
                timestamp=int(time.time()),
                raw_accuracy=neuron_state.raw_accuracy,
            )
            self._save_state()

            bt.logging.success(
                f"New SOTA achieved by {neuron_state.hotkey}! "
                f"Score: {neuron_state.sota_relative_score:.4f}, "
                f"Raw Accuracy: {neuron_state.raw_accuracy:.4f}, "
                f"Proof Size: {neuron_state.proof_size:.2f} bytes, "
                f"Response Time: {neuron_state.response_time:.4f}s"
            )

            self.recalculate_miner_scores(miner_states)
        except Exception as e:
            bt.logging.error(f"Error preserving SOTA circuit: {e}")

    def recalculate_miner_scores(self, miner_states: dict[str, NeuronState]) -> None:
        """Recalculate all miner scores relative to the new SOTA."""
        try:
            with open(
                os.path.join(
                    os.path.dirname(self.sota_directory), "competition_config.json"
                )
            ) as f:
                config = json.load(f)
                weights = config["evaluation"]["scoring_weights"]
        except Exception as e:
            bt.logging.error(
                f"Error loading scoring weights for recalculation, using defaults: {e}"
            )
            weights = {"accuracy": 0.4, "proof_size": 0.3, "response_time": 0.3}

        try:
            for hotkey, state in miner_states.items():
                if not state.verification_result or state.raw_accuracy == 0:
                    state.sota_relative_score = 0.0
                    continue

                accuracy_diff = max(
                    0, self.sota_state.raw_accuracy - state.raw_accuracy
                )

                if self.sota_state.proof_size > 0:
                    proof_size_diff = max(
                        0,
                        (state.proof_size - self.sota_state.proof_size)
                        / self.sota_state.proof_size,
                    )
                else:
                    proof_size_diff = 0 if state.proof_size == 0 else 1

                if self.sota_state.response_time > 0:
                    response_time_diff = max(
                        0,
                        (state.response_time - self.sota_state.response_time)
                        / self.sota_state.response_time,
                    )
                else:
                    response_time_diff = 0 if state.response_time == 0 else 1

                total_diff = torch.tensor(
                    accuracy_diff * weights["accuracy"]
                    + proof_size_diff * weights["proof_size"]
                    + response_time_diff * weights["response_time"]
                )

                state.sota_relative_score = torch.exp(-total_diff).item()
        except Exception as e:
            bt.logging.error(f"Error recalculating miner scores: {e}")

    def check_if_sota(
        self,
        sota_relative_score: float,
        proof_size: float,
        response_time: float,
        improvements: dict = None,
    ) -> bool:
        EPSILON = 1e-6
        MAX_DEGRADATION = 0.1

        if self.sota_state.sota_relative_score == 0:
            return True

        if sota_relative_score < self.sota_state.sota_relative_score - MAX_DEGRADATION:
            return False

        if improvements and "raw" in improvements and "weighted" in improvements:
            if (
                improvements["raw"]["accuracy"] > MAX_DEGRADATION
                or improvements["raw"]["proof_size"] > MAX_DEGRADATION
                or improvements["raw"]["response_time"] > MAX_DEGRADATION
            ):
                return False

            weighted_improvement = sum(-v for v in improvements["weighted"].values())
            return weighted_improvement > EPSILON

        proof_size_degradation = (
            proof_size - self.sota_state.proof_size
        ) / self.sota_state.proof_size
        response_time_degradation = (
            response_time - self.sota_state.response_time
        ) / self.sota_state.response_time

        if (
            proof_size_degradation > MAX_DEGRADATION
            or response_time_degradation > MAX_DEGRADATION
        ):
            return False

        return False

    @property
    def current_state(self) -> SotaState:
        return self.sota_state

from bittensor import logging
from _validator.utils.proof_of_weights import ProofOfWeightsItem
from execution_layer.circuit import Circuit, CircuitType
from constants import (
    BATCHED_PROOF_OF_WEIGHTS_MODEL_ID,
)
from protocol import ProofOfWeightsSynapse, QueryZkProof
from _validator.models.request_type import RequestType


class ProofOfWeightsHandler:
    """
    Handles internal proof of weights
    This covers the case where the origin validator is a validator on Omron;
    no external requests are needed as this internal mechanism is used to generate the proof of weights.
    """

    @staticmethod
    def prepare_pow_request(
        circuit: Circuit, score_manager
    ) -> ProofOfWeightsSynapse | QueryZkProof:
        queue = score_manager.get_pow_queue()
        batch_size = 1024

        if circuit.id != BATCHED_PROOF_OF_WEIGHTS_MODEL_ID:
            logging.debug("Not a batched PoW model. Defaulting to benchmark.")
            return None, False

        if len(queue) < batch_size:
            logging.debug(
                f"Queue is less than {batch_size} items. Defaulting to benchmark."
            )
            return None, False

        pow_items = ProofOfWeightsItem.pad_items(
            queue[:batch_size], target_item_count=batch_size
        )

        logging.info(f"Preparing PoW request for {str(circuit)}")
        score_manager.remove_processed_items(batch_size)
        return (
            ProofOfWeightsHandler._create_request_from_items(circuit, pow_items),
            True,
        )

    @staticmethod
    def _create_request_from_items(
        circuit: Circuit, pow_items: list[ProofOfWeightsItem]
    ) -> ProofOfWeightsSynapse | QueryZkProof:
        inputs = circuit.input_handler(
            RequestType.RWR, ProofOfWeightsItem.to_dict_list(pow_items)
        ).to_json()

        if circuit.metadata.type == CircuitType.PROOF_OF_WEIGHTS:
            return ProofOfWeightsSynapse(
                subnet_uid=circuit.metadata.netuid,
                verification_key_hash=circuit.id,
                proof_system=circuit.proof_system,
                inputs=inputs,
                proof="",
                public_signals="",
            )
        return QueryZkProof(
            query_input={"public_inputs": inputs, "model_id": circuit.id},
            query_output="",
        )

from _validator.models.base_rpc_request import RealWorldRequest
from pydantic import Field
from deployment_layer.circuit_store import circuit_store
from execution_layer.circuit import CircuitType


class ProofOfComputationRPCRequest(RealWorldRequest):
    """
    Request for the Proof of Computation RPC method.
    """

    circuit_id: str = Field(..., description="The ID of the circuit to use")

    class Config:
        arbitrary_types_allowed = True
        extra = "allow"

    def __init__(self, **data):
        circuit_id = data.get("circuit_id")
        if not circuit_id:
            raise ValueError("circuit_id is required")

        circuit = circuit_store.get_circuit(circuit_id)
        if circuit is None:
            raise ValueError(f"No circuit found for ID {circuit_id}")

        if circuit.metadata.type != CircuitType.PROOF_OF_COMPUTATION:
            raise ValueError(
                f"Circuit {circuit_id} is not a proof of computation circuit"
            )

        super().__init__(
            circuit=circuit, inputs=data.get("inputs"), circuit_id=circuit_id
        )

import hashlib
from execution_layer.generic_input import GenericInput


def hash_inputs(inputs: GenericInput | dict) -> str:
    """
    Hashes inputs to proof of weights, excluding dynamic fields.

    Args:
        inputs (dict): The inputs to hash.

    Returns:
        str: The hashed inputs.
    """
    if isinstance(inputs, GenericInput):
        inputs = inputs.to_json()
    filtered_inputs = {
        k: v
        for k, v in inputs.items()
        if k not in ["validator_uid", "nonce", "uid_responsible_for_proof"]
    }
    return hashlib.sha256(str(filtered_inputs).encode()).hexdigest()

from __future__ import annotations

import copy
import traceback
import random

import bittensor as bt

from _validator.api import ValidatorAPI
from _validator.config import ValidatorConfig
from _validator.core.request import Request
from _validator.models.request_type import RequestType
from _validator.pow.proof_of_weights_handler import ProofOfWeightsHandler
from _validator.scoring.score_manager import ScoreManager
from _validator.utils.hash_guard import HashGuard
from constants import (
    BATCHED_PROOF_OF_WEIGHTS_MODEL_ID,
    SINGLE_PROOF_OF_WEIGHTS_MODEL_ID,
)
from deployment_layer.circuit_store import circuit_store
from execution_layer.circuit import Circuit, CircuitType
from execution_layer.generic_input import GenericInput
from protocol import ProofOfWeightsSynapse, QueryZkProof
from utils.wandb_logger import safe_log
from execution_layer.base_input import BaseInput


class RequestPipeline:
    def __init__(
        self, config: ValidatorConfig, score_manager: ScoreManager, api: ValidatorAPI
    ):
        self.config = config
        self.score_manager = score_manager
        self.api = api
        self.hash_guard = HashGuard()

    def prepare_requests(self, filtered_uids) -> list[Request]:
        """
        Prepare requests for the current validation step.
        This includes both regular benchmark requests and any external requests.

        Args:
            filtered_uids (list): List of UIDs to send requests to.

        Returns:
            list[Request]: List of prepared requests.
        """
        if len(filtered_uids) == 0:
            bt.logging.error("No UIDs to query")
            return []

        if self.api.external_requests_queue:
            return self._prepare_real_world_requests(filtered_uids)
        return self._prepare_benchmark_requests(filtered_uids)

    def _check_and_create_request(
        self,
        uid: int,
        synapse: ProofOfWeightsSynapse | QueryZkProof,
        circuit: Circuit,
        request_type: RequestType,
        request_hash: str | None = None,
        save: bool = False,
    ) -> Request | None:
        """Check hash and create request if valid."""
        try:
            if isinstance(synapse, ProofOfWeightsSynapse):
                input_data = synapse.inputs
            else:
                input_data = synapse.query_input["public_inputs"]

            self.hash_guard.check_hash(input_data)
        except Exception as e:
            bt.logging.error(f"Hash already exists: {e}")
            safe_log({"hash_guard_error": 1})
            if request_type == RequestType.RWR:
                self.api.set_request_result(
                    request_hash, {"success": False, "error": "Hash already exists"}
                )
            return None

        return Request(
            uid=uid,
            axon=self.config.metagraph.axons[uid],
            synapse=synapse,
            circuit=circuit,
            request_type=request_type,
            inputs=GenericInput(RequestType.RWR, input_data),
            request_hash=request_hash,
            save=save,
        )

    def _prepare_real_world_requests(self, filtered_uids: list[int]) -> list[Request]:
        external_request = self.api.external_requests_queue.pop()
        requests = []

        for uid in filtered_uids:
            try:
                synapse, save = self.get_synapse_request(
                    RequestType.RWR, external_request.circuit, external_request
                )
                request = self._check_and_create_request(
                    uid=uid,
                    synapse=synapse,
                    circuit=external_request.circuit,
                    request_type=RequestType.RWR,
                    request_hash=external_request.hash,
                    save=save,
                )
                if request:
                    requests.append(request)
            except Exception as e:
                bt.logging.error(f"Error preparing request for UID {uid}: {e}")
                traceback.print_exc()
                self.api.set_request_result(
                    external_request.hash,
                    {"success": False, "error": "Error preparing request"},
                )
                continue
        return requests

    def _prepare_benchmark_requests(self, filtered_uids: list[int]) -> list[Request]:
        circuit = self.select_circuit_for_benchmark()
        if circuit is None:
            bt.logging.error("No circuit selected")
            return []

        requests = []
        for uid in filtered_uids:
            synapse, save = self.get_synapse_request(RequestType.BENCHMARK, circuit)
            request = self._check_and_create_request(
                uid=uid,
                synapse=synapse,
                circuit=circuit,
                request_type=RequestType.BENCHMARK,
                save=save,
            )
            if request:
                requests.append(request)
        return requests

    def select_circuit_for_benchmark(self) -> Circuit:
        """
        Select a circuit for benchmarking using weighted random selection.
        """
        circuits = list(circuit_store.circuits.values())

        return random.choices(
            circuits,
            weights=[
                (circuit.metadata.benchmark_choice_weight or 0) for circuit in circuits
            ],
            k=1,
        )[0]

    def format_for_query(
        self, inputs: dict[str, object] | BaseInput, circuit: Circuit
    ) -> dict[str, object]:
        if hasattr(inputs, "to_json"):
            inputs = inputs.to_json()
        return {"public_inputs": inputs, "model_id": circuit.id}

    def get_synapse_request(
        self,
        request_type: RequestType,
        circuit: Circuit,
        request: any | None = None,
    ) -> tuple[ProofOfWeightsSynapse | QueryZkProof, bool]:
        inputs = (
            circuit.input_handler(request_type)
            if request_type == RequestType.BENCHMARK
            else circuit.input_handler(
                RequestType.RWR,
                copy.deepcopy(request.inputs),
            )
        )

        if request_type == RequestType.RWR:
            if circuit.metadata.type == CircuitType.PROOF_OF_WEIGHTS:
                return (
                    ProofOfWeightsSynapse(
                        subnet_uid=circuit.metadata.netuid,
                        verification_key_hash=circuit.id,
                        proof_system=circuit.proof_system,
                        inputs=inputs.to_json(),
                        proof="",
                        public_signals="",
                    ),
                    True,
                )
            return (
                QueryZkProof(
                    model_id=circuit.id,
                    query_input=self.format_for_query(inputs, circuit),
                    query_output="",
                ),
                True,
            )

        if circuit.id in [
            SINGLE_PROOF_OF_WEIGHTS_MODEL_ID,
            BATCHED_PROOF_OF_WEIGHTS_MODEL_ID,
        ]:
            synapse_request, save = ProofOfWeightsHandler.prepare_pow_request(
                circuit, self.score_manager
            )
            if synapse_request:
                return synapse_request, save

        if circuit.metadata.type == CircuitType.PROOF_OF_COMPUTATION:
            return (
                QueryZkProof(
                    model_id=circuit.id,
                    query_input=self.format_for_query(inputs, circuit),
                    query_output="",
                ),
                False,
            )

        return (
            ProofOfWeightsSynapse(
                subnet_uid=circuit.metadata.netuid,
                verification_key_hash=circuit.id,
                proof_system=circuit.proof_system,
                inputs=inputs.to_json(),
                proof="",
                public_signals="",
            ),
            False,
        )

    def prepare_single_request(self, uid: int) -> Request | None:
        """
        Prepare a single request for a specific UID.

        Args:
            uid (int): The UID to prepare a request for.

        Returns:
            Request | None: The prepared request, or None if preparation failed.
        """
        if self.api.external_requests_queue:
            requests = self._prepare_real_world_requests([uid])
        else:
            requests = self._prepare_benchmark_requests([uid])

        return requests[0] if requests else None
