import json
import os

import numpy as np
import openai
import requests
from scipy.spatial.distance import cosine


def get_embedding_from_api(word, model="vicuna-7b-v1.1"):
    if "ada" in model:
        resp = openai.Embedding.create(
            model=model,
            input=word,
        )
        embedding = np.array(resp["data"][0]["embedding"])
        return embedding

    url = "http://localhost:8000/v1/embeddings"
    headers = {"Content-Type": "application/json"}
    data = json.dumps({"model": model, "input": word})

    response = requests.post(url, headers=headers, data=data)
    if response.status_code == 200:
        embedding = np.array(response.json()["data"][0]["embedding"])
        return embedding
    else:
        print(f"Error: {response.status_code} - {response.text}")
        return None


def cosine_similarity(vec1, vec2):
    return 1 - cosine(vec1, vec2)


def print_cosine_similarity(embeddings, texts):
    for i in range(len(texts)):
        for j in range(i + 1, len(texts)):
            sim = cosine_similarity(embeddings[texts[i]], embeddings[texts[j]])
            print(f"Cosine similarity between '{texts[i]}' and '{texts[j]}': {sim:.2f}")


texts = [
    "The quick brown fox",
    "The quick brown dog",
    "The fast brown fox",
    "A completely different sentence",
]

embeddings = {}
for text in texts:
    embeddings[text] = get_embedding_from_api(text)

print("Vicuna-7B:")
print_cosine_similarity(embeddings, texts)

for text in texts:
    embeddings[text] = get_embedding_from_api(text, model="text-similarity-ada-001")

print("text-similarity-ada-001:")
print_cosine_similarity(embeddings, texts)

for text in texts:
    embeddings[text] = get_embedding_from_api(text, model="text-embedding-ada-002")

print("text-embedding-ada-002:")
print_cosine_similarity(embeddings, texts)

from parrot import P
from parrot.frontend.pfunc.transforms.prompt_formatter import (
    PyIndentRemover,
    SquashIntoOneLine,
    AlwaysOneSpace,
)


def test_py_indent_remover():
    @P.semantic_function(formatter=None)
    def foo(a: P.Output):
        """This is a function.
        It has multiple lines.
        And it has indents. {{a}}
        """

    print("Before:", foo.to_template_str())

    foo = PyIndentRemover().transform(foo)

    print("After:", foo.to_template_str())


def test_squash_into_one_line(formatter=None):
    @P.semantic_function(formatter=None)
    def foo(a: P.Output):
        """This
        is
        a
        function.
        It
        has multiple
        lines. {{a}}
        """

    print("Before:", foo.to_template_str())

    foo = SquashIntoOneLine().transform(foo)

    print("After:", foo.to_template_str())


def test_always_one_space():
    @P.semantic_function(formatter=None)
    def foo(a: P.Output):
        """This is  a   function.    It     has multiple      spaces.   {{a}}"""

    print("Before:", foo.to_template_str())

    foo = AlwaysOneSpace().transform(foo)

    print("After:", foo.to_template_str())


if __name__ == "__main__":
    test_py_indent_remover()
    test_squash_into_one_line()
    test_always_one_space()

import pytest
import inspect
from parrot import P

from parrot.frontend.pfunc.function import PyNativeCall


def test_parse_native_function():
    @P.native_function()
    def add(a: P.Input, b: P.Input, c: P.Output):
        ret = str(int(a) + int(b))
        c.set(ret)

    def add_pyfunc(a: str, b: str) -> str:
        return str(int(a) + int(b))

    print(add.display_signature())
    print(add.inputs)
    print(add.outputs)
    print(inspect.signature(add_pyfunc))


def test_call_to_payload():
    @P.native_function()
    def add(a: str, b: str, c: P.Output):
        ret = str(int(a) + int(b))
        c.set(ret)

    call: PyNativeCall = add("1", "2")
    print(call.to_request_payload())


if __name__ == "__main__":
    # test_parse_native_function()
    test_call_to_payload()

