import json
from pathlib import Path
import pytest

from erdb.utils.attack_power import Attributes, CalculatorData, ArmamentCalculator


"""
Version of the game the sample data has been explicitly collected for
"""
_GAME_VERSION: str = "1.05.0"

_TEST_DIR = Path(__file__).parent.resolve()
_DATA_DIR = _TEST_DIR / "attack_values"
_GAMEDATA_DIR = _TEST_DIR.parent / _GAME_VERSION
_ATTRIBUTE_SETS = [Attributes.from_string(f.stem) for f in _DATA_DIR.glob("*")]

def pytest_generate_tests(metafunc):
    assert "armament" in metafunc.fixturenames
    assert "attribs" in metafunc.fixturenames

    with open(_GAMEDATA_DIR / "armaments.json") as f:
        armaments = json.load(f)["Armaments"]

    armament_data: list[tuple[str, str]] = []
    armament_ids: list[str] = []

    for armament, properties in armaments.items():
        for affinity in properties["affinity"].keys():
            armament_data.append((affinity, armament))
            armament_ids.append(f"{affinity} {armament}")

    metafunc.parametrize("armament", armament_data, ids=armament_ids)
    metafunc.parametrize("attribs", _ATTRIBUTE_SETS, ids=[*map(str, _ATTRIBUTE_SETS)])

@pytest.fixture(scope="module")
def calc_data() -> CalculatorData:
    return CalculatorData.create(_GAMEDATA_DIR)

@pytest.fixture(scope="module")
def results_data() -> dict[str, dict]:
    def load(attribs):
        with open(_DATA_DIR / f"{attribs}.json") as f:
            return json.load(f)

    return {attribs: load(attribs) for attribs in _ATTRIBUTE_SETS}

def test_attack_power(calc_data, armament, attribs, results_data):
    affinity, name = armament
    level = "0" if name == "Meteorite Staff" else "10"

    expected = results_data[attribs][name][affinity]["attack_power"]
    calc = ArmamentCalculator(calc_data, name, affinity, level)
    ap = calc.attack_power(attribs)

    if ap.physical.total > 0:
        assert len(expected["physical"]) == 4
        assert expected["physical"][3] == ap.physical.total

    if ap.magic.total > 0:
        assert len(expected["magic"]) == 4
        assert expected["magic"][3] == ap.magic.total

    if ap.fire.total > 0:
        assert len(expected["fire"]) == 4
        assert expected["fire"][3] == ap.fire.total

    if ap.lightning.total > 0:
        assert len(expected["lightning"]) == 4
        assert expected["lightning"][3] == ap.lightning.total

    if ap.holy.total > 0:
        assert len(expected["holy"]) == 4
        assert expected["holy"][3] == ap.holy.total

import pytest

from erdb.typing.game_version import GameVersion, GameVersionRange


def _make(major: str, minor: str, patch: str) -> GameVersion:
    assert len(minor) >= 2, "Minor part must be at least 2 digits"
    return GameVersion(major, minor, patch, [int(major), int(minor), int(patch)])

@pytest.fixture(scope="module")
def base() -> GameVersion:
    return _make("1", "02", "3")

@pytest.mark.parametrize("left,right", [
    (["1", "02", "3"], ["1", "02", "3"]),
    (["1", "03", "3"], ["1", "03", "3"]),
    (["3", "03", "3"], ["3", "03", "3"]),
])
def test_eq(left: list[str], right: list[str]):
    assert _make(*left) == _make(*right)

@pytest.mark.parametrize("left,right", [
    (["1", "02", "4"], ["1", "02", "3"]),
    (["1", "04", "3"], ["1", "03", "3"]),
    (["4", "03", "3"], ["3", "03", "3"]),
])
def test_ne(left: list[str], right: list[str]):
    assert _make(*left) != _make(*right)

@pytest.mark.parametrize("left", [["1", "02", "2"], ["1", "01", "9"], ["0", "09", "9"]])
def test_lt(base, left):
    assert _make(*left) < base

@pytest.mark.parametrize("left", [["1", "02", "4"], ["1", "03", "0"], ["2", "00", "0"]])
def test_gt(base, left):
    assert _make(*left) > base

@pytest.mark.parametrize("left", [["1", "02", "2"], ["1", "02", "3"]])
def test_le(base, left):
    assert _make(*left) <= base

@pytest.mark.parametrize("left", [["1", "02", "3"], ["1", "02", "4"]])
def test_ge(base, left):
    assert _make(*left) >= base

@pytest.mark.parametrize("string,expected", [
    ("1.04.1", _make("1", "04", "1")),
    ("2.64.9", _make("2", "64", "9")),
    ("0.03.9",  _make("0", "03", "9")),
])
def test_from_string(string: str, expected: GameVersion):
    assert GameVersion.from_string(string) == expected

@pytest.mark.parametrize("string,begin,end", [
    ("from 1.04.1 until 1.05.0", _make("1", "04", "1"), _make("1", "05", "0")),
    ("from 1.04.1", _make("1", "04", "1"), GameVersion.max()),
    ("until 1.05.0", GameVersion.min(), _make("1", "05", "0")),
    ("only 1.04.1", _make("1", "04", "1"), GameVersion.max()),
    ("any version", GameVersion.min(), GameVersion.max()),
])
def test_range_from_string(string: str, begin: GameVersion, end: GameVersion):
    version_range = GameVersionRange.from_string(string)
    assert version_range.begin == begin
    assert version_range.end == end

@pytest.mark.parametrize("string,version", [
    ("from 1.04.1 until 1.05.0", _make("1", "04", "1")),
    ("from 1.04.1 until 1.05.0", _make("1", "04", "2")),
    ("from 1.04.1 until 1.05.0", _make("1", "04", "99")),
    ("from 1.04.1", _make("1", "04", "99")),
    ("from 1.04.1", _make("1", "05", "0")),
    ("until 1.05.0", _make("1", "04", "99")),
    ("until 1.05.0", _make("1", "03", "0")),
    ("only 1.05.0", _make("1", "05", "0")),
    ("any version", _make("1", "04", "99")),
    ("any version", _make("1", "05", "99")),
])
def test_range_contains(string: str, version: GameVersion):
    assert version in GameVersionRange.from_string(string)

@pytest.mark.parametrize("string,version", [
    ("from 1.04.1 until 1.05.0", _make("1", "04", "0")),
    ("from 1.04.1 until 1.05.0", _make("1", "05", "0")),
    ("from 1.04.1 until 1.05.0", _make("1", "05", "99")),
    ("from 1.04.1", _make("1", "04", "0")),
    ("from 1.04.1", _make("1", "03", "99")),
    ("until 1.05.0", _make("1", "05", "0")),
    ("only 1.05.0", _make("1", "04", "99")),
    ("only 1.05.0", _make("1", "05", "1")),
])
def test_range_not_contains(string: str, version: GameVersion):
    assert version not in GameVersionRange.from_string(string)

from typing import List, NamedTuple
import pytest

from erdb.typing.params import ParamDict
from erdb.utils.common import find_offset_indices


class ParamDictGenerator(NamedTuple):
    starting_element: int
    element_interval: int
    levels: List[int]
    level_interval: int

    def generate(self) -> ParamDict:
        def _rangec(start: int, count: int, step: int):
            return range(start, start + count * step, step)

        element_count = len(self.levels)
        element_range = _rangec(self.starting_element, element_count, self.element_interval)

        params: ParamDict = dict()

        for row_id, level_count in zip(element_range, self.levels):
            for offset in _rangec(0, level_count, self.level_interval):
                params[str(row_id + offset)] = {}

        return params

@pytest.mark.parametrize("starting_element,element_interval,levels,level_interval,results", [
    (1000, 100, [11, 26, 26], 1, [range(1000, 1011), range(1100, 1126), range(1200, 1226)]),
    (10000, 10000, [11, 1, 26], 100, [range(10000, 11001, 100), range(20000, 20001, 100), range(30000, 32501, 100)]),
])
def test_find_offset_indices(starting_element: int, element_interval: int, levels: List[int], level_interval: int, results):
    possible_maxima = list(set([l - 1 for l in levels]))
    params = ParamDictGenerator(starting_element, element_interval, levels, level_interval).generate()

    for element in range(len(levels)):
        base = starting_element + element * element_interval
        ids, levels = find_offset_indices(base, params, possible_maxima, level_interval)
        element_offset = starting_element + element_interval * element 

        assert list(results[element]) == list(ids)
        assert [index - element_offset for index in results[element]] == list(levels)

