from typing import Any
from fastapi import Query, status
from fastapi.responses import JSONResponse
from pydantic.dataclasses import dataclass

from erdb.table import Table
from erdb.app_api.common import DataProxy, GameVersionEnum
from erdb.utils.common import getattrstr
from erdb.typing.api_version import ApiVersion


@dataclass
class _Detail:
    detail: str

class DataEndpoint:
    data_proxy: DataProxy
    api: ApiVersion
    table: Table

    def __init__(self, data_proxy: DataProxy, api: ApiVersion, table: Table) -> None:
        self.data_proxy = data_proxy
        self.api = api
        self.table = table

    @property
    def route(self) -> str:
        return "/"

    @property
    def model(self) -> Any:
        return dict[str, self.table.spec.model[self.api]] # type: ignore

    @property
    def summary(self) -> str:
        return "multiple items"

    @property
    def description(self) -> str:
        return "Retrieve a dictionary of item's ascii name -> item properties."

    @property
    def responses(self) -> dict[int, dict]:
        return {
            status.HTTP_400_BAD_REQUEST: {
                "model": _Detail,
                "description": "Bad Request: `query` format is valid, but the specified field does not exist for this model."
            }
        }

    def __call__(self,
        game_version: GameVersionEnum,
        keys: list[str] | None = Query(None, alias="k", description="Specify a list of keys (ascii names) to retrieve specific items."),
        query: str | None = Query(None, description="Filter elements by field in format \"{field}:{value}\".", regex=r"^\w+\:.+$"),
    ) -> Any:
        data = self.data_proxy.generate(self.api, game_version, self.table)

        if keys is not None:
            data = {k: v for k, v in data.items() if k in keys}

        if query is not None:
            field, value = query.split(":", maxsplit=1)

            try:
                data = {k: v for k, v in data.items() if getattrstr(v, field) == value}

            except AttributeError:
                return JSONResponse(status_code=status.HTTP_400_BAD_REQUEST, content={"detail": f"{self.table.title} has no field: \"{field}\"."})

        return data

class ItemEndpoint:
    data_proxy: DataProxy
    api: ApiVersion
    table: Table

    def __init__(self, data_proxy: DataProxy, api: ApiVersion, table: Table) -> None:
        self.data_proxy = data_proxy
        self.api = api
        self.table = table

    @property
    def route(self) -> str:
        return "/{key}"

    @property
    def model(self) -> Any:
        return self.table.spec.model[self.api]

    @property
    def summary(self) -> str:
        return "single item"

    @property
    def description(self) -> str:
        return "Retrieve properties of a single item."

    @property
    def responses(self) -> dict[int, dict]:
        return {
            status.HTTP_404_NOT_FOUND: {"model": _Detail}
        }

    def __call__(self, game_version: GameVersionEnum, key: str) -> Any:
        try:
            return self.data_proxy.generate(self.api, game_version, self.table)[key]

        except KeyError:
            return JSONResponse(status_code=status.HTTP_404_NOT_FOUND, content={"detail": f"{self.table.title} has no key: \"{key}\""})

import erdb.effect_parser.attribute_fields as attrib_fields
import erdb.effect_parser.parsers as parse
import erdb.effect_parser.hardcoded as hardcoded_effects
from erdb.effect_parser.aggregator import aggregate_effects
from erdb.typing.models.effect import StatusEffects
from erdb.typing.params import ParamRow, ParamDict
from erdb.typing.enums import SpEffectType, AttackCondition
from erdb.typing.effects import SchemaEffect


_REFERENCE_EFFECT_PARAMS: list[str] = ["cycleOccurrenceSpEffectId", "applyIdOnGetSoul"]

_SP_EFFECT_TO_FIELD: dict[SpEffectType, str] = {
    SpEffectType.HEMORRHAGE: "bloodAttackPower",
    SpEffectType.FROSTBITE: "freezeAttackPower",
    SpEffectType.POISON: "poizonAttackPower",
    SpEffectType.SCARLET_ROT: "diseaseAttackPower",
    SpEffectType.SLEEP: "sleepAttackPower",
    SpEffectType.MADNESS: "madnessAttackPower",
    SpEffectType.BLIGHT: "curseAttackPower",
}

_SP_EFFECT_TO_STR: dict[SpEffectType, str] = {
    SpEffectType.HEMORRHAGE: "bleed",
    SpEffectType.FROSTBITE: "frostbite",
    SpEffectType.POISON: "poison",
    SpEffectType.SCARLET_ROT: "scarlet_rot",
    SpEffectType.SLEEP: "sleep",
    SpEffectType.MADNESS: "madness",
    SpEffectType.BLIGHT: "death_blight",
}

def get_effects(sp_effect: ParamRow, sp_effect_type: SpEffectType, triggeree: ParamRow | None = None, init_conditions: list[str] | None = None) -> list[SchemaEffect]:
    effects = hardcoded_effects.get(sp_effect.index, sp_effect_type)

    for field, attrib_field in attrib_fields.get().items():
        if sp_effect[field] == str(attrib_field.default_value):
            continue

        effect = SchemaEffect.from_attribute_field(sp_effect[field].as_float, attrib_field)

        effect.conditions = init_conditions
        if conds := parse.conditions(sp_effect, triggeree):
            effect.conditions = conds if effect.conditions is None else effect.conditions + conds

        effect.tick_interval = parse.interval(sp_effect)
        effect.value_pvp = parse.value_pvp(sp_effect, field, attrib_fields.get())

        effects.append(effect)

    return effects

def get_effects_nested(sp_effect: ParamRow, sp_effects: ParamDict, add_condition: AttackCondition | None) -> list[SchemaEffect]:
    sp_effect_type = SpEffectType(sp_effect["stateInfo"])
    effects = get_effects(sp_effect, sp_effect_type, init_conditions=[str(add_condition)] if add_condition else None)

    for ref_id in (sp_effect[ref_field].as_int for ref_field in _REFERENCE_EFFECT_PARAMS):
        if ref_sp_effect := sp_effects.get(ref_id):
            if ref_sp_effect.index > 0:
                effects += get_effects(ref_sp_effect, sp_effect_type, sp_effect)

    for condition_offset in hardcoded_effects.get_conditions(sp_effect.index):
        ref_sp_effect = sp_effects[sp_effect.index + condition_offset.offset]
        init_conditions = None if condition_offset.condition is None else [str(condition_offset.condition)]
        effects += get_effects(ref_sp_effect, sp_effect_type, sp_effect, init_conditions)

    return effects

def get_status_effect(sp_effect: ParamRow) -> tuple[str, int]:
    # NOTE: not identifying effects by values, relying on `stateInfo` to be correct at all times
    etype = SpEffectType(sp_effect["stateInfo"])
    return _SP_EFFECT_TO_STR[etype], sp_effect[_SP_EFFECT_TO_FIELD[etype]].as_int

def parse_effects(row: ParamRow, sp_effects: ParamDict, *effect_referencing_fields: str, add_condition: AttackCondition | None = None) -> list[dict]:
    effects: list[SchemaEffect] = []

    for effect_id in (row[ref_field].as_int for ref_field in effect_referencing_fields):
        if effect_id in hardcoded_effects.get_status_effect_ranges():
            continue

        if effect_id in sp_effects:
            effects += get_effects_nested(sp_effects[effect_id], sp_effects, add_condition)

    return [e.to_dict() for e in aggregate_effects(effects)]

def parse_status_effects(effect_ids: list[int], sp_effects: ParamDict) -> StatusEffects:
    # Getting 0th effect if value no found, bug with Antspur Rapier -- get anything to return a 0 status effect
    effects = [sp_effects.get(i, sp_effects[0]) for i in effect_ids if i != -1]
    status_effects = hardcoded_effects.get_status_effect_ranges()
    return StatusEffects(**dict([get_status_effect(e) for e in effects if e.index in status_effects]))

def parse_weapon_effects(weapon: ParamRow) -> list[dict]:
    effects: list[SchemaEffect] = []

    for field, attrib_field in attrib_fields.get(weapon=True).items():
        if weapon[field] != str(attrib_field.default_value):
            effects.append(SchemaEffect.from_attribute_field(weapon[field].as_float, attrib_field))

    return [e.to_dict() for e in effects]

import operator as op
from types import SimpleNamespace
from typing import NamedTuple, Self

from erdb.typing.effects import AttributeName, SchemaEffect


_A = AttributeName

class AttributeAggregatorHint(NamedTuple):
    base: set[AttributeName]
    effective: AttributeName

class AggregatedSchemaEffect(SimpleNamespace):
    attribute_names: set[AttributeName]
    example_effect: SchemaEffect

    @classmethod
    def from_effect(cls, effect: SchemaEffect) -> Self:
        return cls(attribute_names={effect.attribute}, example_effect=effect)

"""
Specifies which attribute sets can be collapsed into their effective attribute.
ORDER IS IMPORTANT because effective attributes are taken into consideration in
consecutive iterations.
"""
_AGGREGATOR_HINTS: list[AttributeAggregatorHint] = [
    AttributeAggregatorHint(
        base={_A.STANDARD_ABSORPTION, _A.STRIKE_ABSORPTION, _A.SLASH_ABSORPTION, _A.PIERCE_ABSORPTION},
        effective=_A.PHYSICAL_ABSORPTION
    ),
    AttributeAggregatorHint(
        base={_A.MAGIC_ABSORPTION, _A.FIRE_ABSORPTION, _A.LIGHTNING_ABSORPTION, _A.HOLY_ABSORPTION},
        effective=_A.ELEMENTAL_ABSORPTION
    ),
    AttributeAggregatorHint(
        base={_A.PHYSICAL_ABSORPTION, _A.ELEMENTAL_ABSORPTION},
        effective=_A.ABSORPTION
    ),
    AttributeAggregatorHint(
        base={_A.STANDARD_ATTACK_POWER, _A.STRIKE_ATTACK_POWER, _A.SLASH_ATTACK_POWER, _A.PIERCE_ATTACK_POWER},
        effective=_A.PHYSICAL_ATTACK_POWER
    ),
    AttributeAggregatorHint(
        base={_A.MAGIC_ATTACK_POWER, _A.FIRE_ATTACK_POWER, _A.LIGHTNING_ATTACK_POWER, _A.HOLY_ATTACK_POWER},
        effective=_A.ELEMENTAL_ATTACK_POWER
    ),
    AttributeAggregatorHint(
        base={_A.PHYSICAL_ATTACK_POWER, _A.ELEMENTAL_ATTACK_POWER},
        effective=_A.ATTACK_POWER
    ),
    AttributeAggregatorHint(
        base={_A.POISON_RESISTANCE, _A.SCARLET_ROT_RESISTANCE},
        effective=_A.IMMUNITY
    ),
    AttributeAggregatorHint(
        base={_A.BLEED_RESISTANCE, _A.FROSTBITE_RESISTANCE},
        effective=_A.ROBUSTNESS
    ),
    AttributeAggregatorHint(
        base={_A.SLEEP_RESISTANCE, _A.MADNESS_RESISTANCE},
        effective=_A.FOCUS
    ),
    AttributeAggregatorHint(
        base={_A.DEATH_BLIGHT_RESISTANCE},
        effective=_A.VITALITY
    ),
    AttributeAggregatorHint(
        base={_A.SORCERY_FOCUS_CONSUMPTION, _A.INCANTATION_FOCUS_CONSUMPTION, _A.PYROMANCY_FOCUS_CONSUMPTION},
        effective=_A.SPELL_FOCUS_CONSUMPTION
    ),
    AttributeAggregatorHint(
        base={_A.SORCERY_FOCUS_CONSUMPTION, _A.INCANTATION_FOCUS_CONSUMPTION}, # since pyromancies are unused
        effective=_A.SPELL_FOCUS_CONSUMPTION
    ),
]

def _get_aggregated_effects(effects: list[SchemaEffect]) -> dict[int, AggregatedSchemaEffect]:
    aggregated_effects: dict[int, AggregatedSchemaEffect] = dict()

    for effect in effects:
        if (key := effect.get_values_hash()) in aggregated_effects:
            aggregated_effects[key].attribute_names.add(effect.attribute)
        else:
            aggregated_effects[key] = AggregatedSchemaEffect.from_effect(effect)

    return aggregated_effects

def _aggregate_attributes(attributes: set[AttributeName], hints: list[AttributeAggregatorHint]) -> set[AttributeName]:
    for hint in hints:
        if hint.base.issubset(attributes):
            attributes.difference_update(hint.base)
            attributes.add(hint.effective)

    return attributes

def _aggregated_effects_to_effects(aggregated_effects: dict[int, AggregatedSchemaEffect]) -> list[SchemaEffect]:
    effects = []

    for aggregated_effect in aggregated_effects.values():
        for attribute_name in aggregated_effect.attribute_names:
            effects.append(aggregated_effect.example_effect.clone(attribute_name))

    return effects

def aggregate_effects(base_effects: list[SchemaEffect]) -> list[SchemaEffect]:
    aggregated_effects = _get_aggregated_effects(base_effects)

    for key, aggregated_effect in aggregated_effects.items():
        aggregated_effects[key].attribute_names = _aggregate_attributes(aggregated_effect.attribute_names, _AGGREGATOR_HINTS)

    return sorted(_aggregated_effects_to_effects(aggregated_effects), key=op.attrgetter("attribute"))

import math
from erdb.typing.params import ParamRow
from erdb.typing.enums import SpEffectType, AttackType
from erdb.typing.effects import AttributeField, EffectModel


_TRIGGER_FIELDS = [
    "stateInfo", "invocationConditionsStateChange1",
    "invocationConditionsStateChange2", "invocationConditionsStateChange3"
]

_CONDITION_FIELDS = [
    "magicSubCategoryChange1", "magicSubCategoryChange2", "magicSubCategoryChange3"
]

_PVE_TO_PVP = {
    "defEnemyDmgCorrectRate_Physics": "defPlayerDmgCorrectRate_Physics",
    "defEnemyDmgCorrectRate_Magic":   "defPlayerDmgCorrectRate_Magic",
    "defEnemyDmgCorrectRate_Fire":    "defPlayerDmgCorrectRate_Fire",
    "defEnemyDmgCorrectRate_Thunder": "defPlayerDmgCorrectRate_Thunder",
    "defEnemyDmgCorrectRate_Dark":    "defPlayerDmgCorrectRate_Dark",
    "atkEnemyDmgCorrectRate_Physics": "atkPlayerDmgCorrectRate_Physics",
    "atkEnemyDmgCorrectRate_Magic":   "atkPlayerDmgCorrectRate_Magic",
    "atkEnemyDmgCorrectRate_Fire":    "atkPlayerDmgCorrectRate_Fire",
    "atkEnemyDmgCorrectRate_Thunder": "atkPlayerDmgCorrectRate_Thunder",
    "atkEnemyDmgCorrectRate_Dark":    "atkPlayerDmgCorrectRate_Dark",
}

def _floor_decimal_2(value: float) -> float:
    value = round(value, 6) # do not floor cases like 1.89999999999
    return math.floor(value * 100) / 100.0

def conditions(sp_effect: ParamRow, triggeree: ParamRow | None = None) -> list[str] | None:
    conds = set()

    def _append_triggers(source: ParamRow):
        for trigger in _TRIGGER_FIELDS:
            effect_type = SpEffectType(source[trigger])

            if not effect_type.is_passive():
                conds.add(str(effect_type))

            if effect_type == SpEffectType.SPELL_POWER_BOOST:
                boostSorcery = ("magParamChange", "Affects Sorceries")
                boostIncantation = ("miracleParamChange", "Affects Incantations")
                for field, cond in [boostSorcery, boostIncantation]:
                    if sp_effect[field].as_bool:
                        conds.add(cond)

    def _append_conditions(source: ParamRow):
        for cond in _CONDITION_FIELDS:
            attack_type_str = source[cond]
            if (cond := AttackType(attack_type_str)) != AttackType.NONE:
                conds.add(str(cond))

    for field, direction in [("conditionHp", "below"), ("conditionHpRate", "above")]:
        if cond := sp_effect[field].get_int():
            conds.add(f"HP {direction} {cond}%")

    _append_triggers(sp_effect)
    _append_conditions(sp_effect)

    if triggeree:
        _append_triggers(triggeree)
        _append_conditions(triggeree)

    return None if len(conds) == 0 else sorted(list(conds))

def interval(sp_effect: ParamRow) -> float | None:
    interv = sp_effect["motionInterval"]
    return None if interv == "0" else float(interv)

def value_pvp(sp_effect: ParamRow, field_pve: str, attrib_fields: dict[str, AttributeField]) -> float | None:
    if not (field_pvp := _PVE_TO_PVP.get(field_pve, None)):
        return None

    attrib_field = attrib_fields[field_pve]
    val = sp_effect[field_pvp].as_float

    return attrib_field.parser(val, attrib_field.effect_model)

def generic(value: float, model: EffectModel) -> float:
    return value

def generic_inverse(value: float, model: EffectModel) -> float:
    """
    Some sp_effect fields are hardcoded to be subtractable/divisible. Because of this,
    their values are negative in the params. Specifying this parser will reverse
    these values on per effect basis to have them make more sense.

    Examples:
    - Blessed Dew Talisman's `changeHpPoint` is set to -2 when it should heal
    - Malenia - Scarlet Rot's `changeHpPoint` is set to 26 when it should damage
    """
    return -value if model == EffectModel.ADDITIVE else _floor_decimal_2(2 - value)

def generic_inverse_percentage(value: float, model: EffectModel) -> float:
    """
    Flat percentage value, but also inverse. Turn -10 into 1.1 (ex. Assassin's Crimson Dagger).
    """
    return 1 + (-value / 100.0)

def poise(value: float, model: EffectModel) -> float:
    """
    Poise value is given as a "poise damage absorption" value, eg. `0.75`.
    The actual poise increase that is shown in game is the inverse of that:
        1 / .75 -> +33% poise.
    """
    return math.floor(1 / value * 100) / 100.0

def item_discovery(value: float, model: EffectModel) -> float:
    return value * 100

import json
from pathlib import Path

from erdb.typing.game_version import GameVersion, GameVersionRange
from erdb.loaders import PKG_DATA_PATH


def _overlay_properties(ret: dict, source: dict):
    """
    Append to lists and sets, override scalars and dictionaries
    """
    for key, value in source.items():
        if isinstance(value, list):
            ret[key] = ret.get(key, list()) + value
        elif isinstance(value, set):
            ret[key] = ret.get(key, set()) + value # type: ignore
        else:
            ret[key] = value

def _parse_user_file(path: Path, version: GameVersion) -> dict:
    assert path.is_file
    assert path.suffix == ".json"

    with open(path, "r", encoding="utf-8") as f:
        data = json.load(f)

    ret = dict()

    for version_range, properties in data.items():
        if version in GameVersionRange.from_string(version_range):
            _overlay_properties(ret, properties)

    return ret

def load(element: str, version: GameVersion) -> dict[str, dict]:
    path = PKG_DATA_PATH / "contrib" / element
    files = path.iterdir() if path.is_dir() else []
    return {f.stem: _parse_user_file(f, version) for f in files}

from typing import Iterator
import xml.etree.ElementTree as xmltree
from csv import DictReader
from zipfile import Path as ZipPath

from erdb.loaders import PKG_DATA_PATH
from erdb.typing.game_version import GameVersion
from erdb.typing.params import ParamRow, ParamDict
from erdb.typing.enums import ItemIDFlag


def _load(param: str, version: GameVersion) -> Iterator[dict[str, str]]:
    archive = PKG_DATA_PATH / "gamedata" / f"{version}.zip"
    with ZipPath(archive, at=f"{param}.csv").open(mode="r") as f:
        yield from DictReader(f, delimiter=";") # type: ignore

def load(param: str, version: GameVersion, item_id_flag: ItemIDFlag) -> ParamDict:
    return {int(row["Row ID"]): ParamRow.make(row, item_id_flag) for row in _load(param, version)}

# optimal variant for params with a lot of IDs like spEffects
def load_ids(param: str, version: GameVersion, item_id_flag: ItemIDFlag, id_min: int, id_max: int = 999999999) -> ParamDict:
    return {int(row["Row ID"]): ParamRow.make(row, item_id_flag) for row in _load(param, version) if id_min <= int(row["Row ID"]) <= id_max}

def load_msg(filename: str, version: GameVersion) -> dict[int, str]:
    archive = PKG_DATA_PATH / "gamedata" / f"{version}.zip"
    with ZipPath(archive, at=f"{filename}.fmg.xml").open(mode="r", encoding="utf-8") as f:
        tree = xmltree.fromstring(f.read())
        entries = tree.findall(".//text")

    return {int(str(e.get("id"))): str(e.text) for e in entries if e.text != "%null%"}

