            json={
                "key": "my_email_config",
                "name": "mailgun",
                "service_type": EmailServiceType.MAILGUN.value,
                "details": {EmailServiceDetails.DOMAIN.value: "my.mailgun.domain"},
            },
        )
        assert response.status_code == 400
        assert response.json() == {
