        "requested_at": datetime(
            2018,
            12,
            31,
            hour=2,
            minute=30,
            second=23,
            microsecond=916482,
            tzinfo=timezone.utc,
        ),
        "status": status,
        "origin": f"https://example.com/",
        "policy_id": policy.id,
        "client_id": policy.client_id,
    }
    if status != PrivacyRequestStatus.pending:
        data["started_processing_at"] = datetime(
            2019,
            1,
            1,
            hour=1,
            minute=45,
            second=55,
            microsecond=393185,
            tzinfo=timezone.utc,
        )
    pr = PrivacyRequest.create(
        db=db,
        data=data,
    )
    email_identity = "test@example.com"
    identity_kwargs = {"email": email_identity}
    pr.cache_identity(identity_kwargs)
    pr.persist_identity(
        db=db,
        identity=Identity(
            email=email_identity,
            phone_number="+1 234 567 8910",
        ),
    )
    return pr


