    return JSONResponse(
        content={"error": "Sorry, we can't find the page you are looking for."},
        headers={
            "x-error-code": "RepoNotFound",
            "x-error-message": "Sorry, we can't find the page you are looking for.",
        },
        status_code=404,
    )


