    return Response(
        headers={
            "x-error-code": "EntryNotFound",
            "x-error-message": f'{path} does not exist on "{branch}"',
        },
        status_code=404,
    )


def error_entry_not_found() -> Response:
    return Response(
        headers={
            "x-error-code": "EntryNotFound",
            "x-error-message": "Entry not found",
        },
        status_code=404,
    )


