    async def open(self, mode: OpenTextMode = ..., buffering: int = ...,
                   encoding: Optional[str] = ..., errors: Optional[str] = ...,
                   newline: Optional[str] = ...) -> AsyncFile[str]:
        ...

    async def open(self, mode: str = 'r', buffering: int = -1, encoding: Optional[str] = None,
